import React, { useState } from 'react';
import FixedCosts from './components/FixedCosts';
import VariableCosts from './components/VariableCosts';
import TotalCosts from './components/TotalCosts';
import './styles.css';

function App() {
    const [fixedCosts, setFixedCosts] = useState({
        diasTrabalhados: 0,
        gastofixo: 0,
        ipva: 0,
        seguro: 0,
    });

    const [variableCosts, setVariableCosts] = useState({
        kmDiarios: 0,
        kmPorLitro: 0,
        precoCombustivel: 0,
        custoManutencao: 0,
        custoPneus: 0,
        tempoSubstituicaoPneus: 0,
        emergencyMaintenance: 0,
    });

    const handleFixedCostChange = (e) => {
        const { id, value } = e.target;
        setFixedCosts((prev) => ({ ...prev, [id]: Number(value) }));
    };

    const handleVariableCostChange = (e) => {
        const { id, value } = e.target;
        setVariableCosts((prev) => ({ ...prev, [id]: Number(value) }));
    };

    return (
        <div className="App">
            <FixedCosts costs={fixedCosts} onChange={handleFixedCostChange} />
            <VariableCosts costs={variableCosts} onChange={handleVariableCostChange} />
            <TotalCosts fixedCosts={fixedCosts} variableCosts={variableCosts} />
        </div>
    );
}

export default App;
