import React, { useState } from 'react';

const VariableCosts = ({ onVariableCostsChange }) => {
    const [kmPerDay, setKmPerDay] = useState('');
    const [kmPerLiter, setKmPerLiter] = useState('');
    const [fuelPrice, setFuelPrice] = useState('');
    const [maintenanceCost, setMaintenanceCost] = useState('');
    const [tireCost, setTireCost] = useState('');
    const [tireReplacementTime, setTireReplacementTime] = useState('');
    const [emergencyMaintenance, setEmergencyMaintenance] = useState('');

    const handleInputChange = () => {
        onVariableCostsChange({
            kmPerDay,
            kmPerLiter,
            fuelPrice,
            maintenanceCost,
            tireCost,
            tireReplacementTime,
            emergencyMaintenance,
        });
    };

    return (
        <div className="gastovariavel" id="gastovariavel">
            <h2>Gastos variaveis</h2>

            <h3>Combustível</h3>
            <p>Quantos km por dia você roda?</p>
            <input
                type="number"
                value={kmPerDay}
                onChange={(e) => {
                    setKmPerDay(e.target.value);
                    handleInputChange();
                }}
            />

            <p>Quanto seu carro faz por litro?</p>
            <input
                type="number"
                value={kmPerLiter}
                onChange={(e) => {
                    setKmPerLiter(e.target.value);
                    handleInputChange();
                }}
            />

            <p>Valor do combustível</p>
            <input
                type="number"
                value={fuelPrice}
                onChange={(e) => {
                    setFuelPrice(e.target.value);
                    handleInputChange();
                }}
            />

            <p>Quanto custa uma revisão no seu carro?</p>
            <input
                type="number"
                value={maintenanceCost}
                onChange={(e) => {
                    setMaintenanceCost(e.target.value);
                    handleInputChange();
                }}
            />

            <p>Quanto custa um jogo de pneu do seu carro?</p>
            <input
                type="number"
                value={tireCost}
                onChange={(e) => {
                    setTireCost(e.target.value);
                    handleInputChange();
                }}
            />

            <p>Em quanto tempo você precisa trocar o jogo de pneu?</p>
            <input
                type="number"
                value={tireReplacementTime}
                onChange={(e) => {
                    setTireReplacementTime(e.target.value);
                    handleInputChange();
                }}
            />

            <p>Quanto você guarda para manutenção de emergência?</p>
            <input
                type="number"
                value={emergencyMaintenance}
                onChange={(e) => {
                    setEmergencyMaintenance(e.target.value);
                    handleInputChange();
                }}
            />
        </div>
    );
};

export default VariableCosts;
