import React, { useState } from 'react';
import FixedCosts from './components/FixedCosts';
import VariableCosts from './components/VariableCosts';
import TotalCosts from './components/TotalCosts';
import './styles.css';

function App() {
    const [fixedCosts, setFixedCosts] = useState({
        diasTrabalhados: 0,
        gastofixo: 0,
        ipva: 0,
        seguro: 0,
    });

    const [variableCosts, setVariableCosts] = useState({
        kmDiarios: 0,
        kmPorLitro: 0,
        precoCombustivel: 0,
        custoManutencao: 0,
        custoPneus: 0,
        tempoSubstituicaoPneus: 0,
        manutencaoEmergencia: 0,
        });

    const handleFixedCostChange = (e) => {
        const { id, value } = e.target;
        setFixedCosts((prev) => ({ ...prev, [id]: Number(value) }));
    };

    const handleVariableCostChange = (e) => {
        const { id, value } = e.target;
        setVariableCosts((prev) => ({ ...prev, [id]: Number(value) }));
    };

    const calculateTotalCosts = () => {
        const totalFixed = (fixedCosts.gastofixo / fixedCosts.diasTrabalhados) + ((fixedCosts.ipva / 12) / fixedCosts.diasTrabalhados) + (fixedCosts.seguro / fixedCosts.diasTrabalhados);


        const gastoComCombustivel = (variableCosts.kmDiarios / variableCosts.kmPorLitro) * variableCosts.precoCombustivel;
        const gastoComRevisão = (variableCosts.custoManutencao / 10000) * variableCosts.kmDiarios;
        const gastoComPneus = (variableCosts.custoPneus / variableCosts.tempoSubstituicaoPneus) * variableCosts.kmDiarios;
        const ManutençãoEmergencial = variableCosts.manutencaoEmergencia / fixedCosts.diasTrabalhados;

        const totalVariable = gastoComCombustivel + gastoComRevisão + gastoComPneus + ManutençãoEmergencial;

        // console.log("gasto com pneu:", gastoComPneus.toFixed(2));
        // console.log("gasto com revisão:", gastoComRevisão.toFixed(2));
        // console.log("gasto com combustivel:", gastoComCombustivel.toFixed(2));
        // console.log("gasto com manutenção emergencial:", ManutençãoEmergencial.toFixed(2));


        const totalDaily = totalFixed + totalVariable;
        const costPerKm = totalDaily / variableCosts.kmDiarios || 0;
        const paymentPerKm = costPerKm / 0.40;

        const metaDiaria = TotalCosts.metaDiaria;

        console.log(metaDiaria)

        return { totalDaily, costPerKm, paymentPerKm, metaDiaria };
    };

    const { totalDaily, costPerKm, paymentPerKm } = calculateTotalCosts();

    return (
        <div>
            <h1>Contas iFood</h1>
            <FixedCosts fixedCosts={fixedCosts} onChange={handleFixedCostChange} />
            <VariableCosts variableCosts={variableCosts} onChange={handleVariableCostChange} />
            <TotalCosts totalDaily={totalDaily} costPerKm={costPerKm} paymentPerKm={paymentPerKm} />
        </div>
    );
}

export default App;
