import React, { useState } from 'react';
import FixedCosts from './components/FixedCosts';
import VariableCosts from './components/VariableCosts';
import TotalCosts from './components/TotalCosts';
import './styles.css';

function App() {
    const [fixedCosts, setFixedCosts] = useState({
        diasTrabalhados: "",
        gastofixo: "",
        ipva: "",
        seguro: "",
    });

    const [variableCosts, setVariableCosts] = useState({
        kmDiarios: "",
        kmPorLitro: "",
        precoCombustivel: "",
        custoManutencao: "",
        custoPneus: "",
        tempoSubstituicaoPneus: "",
        manutencaoEmergencia: "",
    });

    const handleFixedCostChange = (e) => {
        const { id, value } = e.target;
        setFixedCosts((prev) => ({ ...prev, [id]: value }));
    };

    const handleVariableCostChange = (e) => {
        const { id, value } = e.target;
        let normalizedValue = value;
        if (id === "kmPorLitro") {
            normalizedValue = value.replace(",", ".");
        }
        setVariableCosts((prev) => ({ ...prev, [id]: normalizedValue }));
    };

    const calculateTotalCosts = () => {
        const diasTrabalhados = Number(fixedCosts.diasTrabalhados);
        const gastofixo = Number(fixedCosts.gastofixo);
        const ipva = Number(fixedCosts.ipva);
        const seguro = Number(fixedCosts.seguro);

        const kmDiarios = Number(variableCosts.kmDiarios);
        const kmPorLitro = Number(variableCosts.kmPorLitro);
        const precoCombustivel = Number(variableCosts.precoCombustivel);
        const custoManutencao = Number(variableCosts.custoManutencao);
        const custoPneus = Number(variableCosts.custoPneus);
        const tempoSubstituicaoPneus = Number(variableCosts.tempoSubstituicaoPneus);
        const manutencaoEmergencia = Number(variableCosts.manutencaoEmergencia);

        const totalFixed = (gastofixo / diasTrabalhados) + ((ipva / 12) / diasTrabalhados) + (seguro / diasTrabalhados);

        const gastoComCombustivel = (kmDiarios / kmPorLitro) * precoCombustivel;
        const gastoComRevisao = (custoManutencao / 10000) * kmDiarios;
        const gastoComPneus = (custoPneus / tempoSubstituicaoPneus) * kmDiarios;
        const manutencaoEmergencial = manutencaoEmergencia / diasTrabalhados;

        const totalVariable = gastoComCombustivel + gastoComRevisao + gastoComPneus + manutencaoEmergencial;

        const totalDaily = totalFixed + totalVariable;
        const costPerKm = totalDaily / kmDiarios || 0;
        const paymentPerKm = costPerKm / 0.40;

        const metaDiaria = paymentPerKm * kmDiarios;
        const salarioMensal = (metaDiaria * diasTrabalhados) - (costPerKm * diasTrabalhados);

        return { totalDaily, costPerKm, paymentPerKm, metaDiaria, salarioMensal };
    };

    const { totalDaily, costPerKm, paymentPerKm, metaDiaria, salarioMensal } = calculateTotalCosts();

    return (
        <div>
            <h1>Luber</h1>
            <FixedCosts fixedCosts={fixedCosts} onChange={handleFixedCostChange} />
            <VariableCosts variableCosts={variableCosts} onChange={handleVariableCostChange} />
            <TotalCosts totalDaily={totalDaily} costPerKm={costPerKm} paymentPerKm={paymentPerKm} metaDiaria={metaDiaria} salarioMensal={salarioMensal}/>
        </div>
    );
}

export default App;
