import React, { useState } from 'react';
import FixedCosts from './components/FixedCosts';
import VariableCosts from './components/VariableCosts';
import TotalCosts from './components/TotalCosts';
import './styles.css';

function App() {
    const [fixedCosts, setFixedCosts] = useState({
        diasTrabalhados: 0,
        gastofixo: 0,
        ipva: 0,
        seguro: 0,
    });

    const [variableCosts, setVariableCosts] = useState({
        kmDiarios: 0,
        kmPorLitro: 0,
        precoCombustivel: 0,
        custoManutencao: 0,
        custoPneus: 0,
        tempoSubstituicaoPneus: 0,
    });

    const handleFixedCostChange = (e) => {
        const { id, value } = e.target;
        setFixedCosts((prev) => ({ ...prev, [id]: Number(value) }));
    };

    const handleVariableCostChange = (e) => {
        const { id, value } = e.target;
        setVariableCosts((prev) => ({ ...prev, [id]: Number(value) }));
    };

    const calculateTotalCosts = () => {
        const totalFixed = (fixedCosts.gastofixo / fixedCosts.diasTrabalhados) + (fixedCosts.ipva / 12) + (fixedCosts.seguro / fixedCosts.diasTrabalhados);

        console.log(totalFixed.toFixed(2));

        const totalVariable = (variableCosts.kmDiarios / variableCosts.kmPorLitro) * variableCosts.precoCombustivel + variableCosts.custoManutencao + (variableCosts.custoPneus / variableCosts.tempoSubstituicaoPneus);
        const totalDaily = totalFixed + totalVariable;
        const costPerKm = totalDaily / variableCosts.kmDiarios || 0;
        const paymentPerKm = costPerKm; // Assuming payment per km is equal to cost per km



        return { totalDaily, costPerKm, paymentPerKm };
    };

    const { totalDaily, costPerKm, paymentPerKm } = calculateTotalCosts();

    return (
        <div>
            <h1>Contas iFood</h1>
            <FixedCosts fixedCosts={fixedCosts} onChange={handleFixedCostChange} />
            <VariableCosts variableCosts={variableCosts} onChange={handleVariableCostChange} />
            <TotalCosts totalDaily={totalDaily} costPerKm={costPerKm} paymentPerKm={paymentPerKm} />
        </div>
    );
}

export default App;
