import React from 'react';

const TotalCosts = ({ fixedCosts, variableCosts }) => {
    const { parcelacarro, ipva, seguro } = fixedCosts;
    const { kmpordia, kmporlitro, valorcombustivel, revisão, custopneu, trocadepneu, manutencaodeemergencia } = variableCosts;

    const dailyFuelCost = (kmpordia / kmporlitro) * valorcombustivel;
    const totalVariableCosts = dailyFuelCost + revisão + (custopneu / trocadepneu) + manutencaodeemergencia;
    const totalDailyCost = parcelacarro + ipva + seguro + totalVariableCosts;
    const costPerKm = totalDailyCost / kmpordia;
    const paymentPerKm = costPerKm; // Assuming payment per km is equal to cost per km

    return (
        <div className="gastototal" id="gastototal">
            <h2>Gasto total diario</h2>
            <h3 id="gastototaldiario" className="gastototaldiario">{totalDailyCost.toFixed(2)}</h3>

            <h2>Custo por KM</h2>
            <h3 id="gastoporkm" className="gastoporkm">{costPerKm.toFixed(2)}</h3>

            <h1>Corridas tem que pagar:</h1>
            <h1 id="pagamentoporkm" className="pagamentoporkm">{paymentPerKm.toFixed(2)}</h1>
        </div>
    );
};

export default TotalCosts;
